package net.hypejet.jet.server.test.network.protocol.packet.server.status;

import net.hypejet.jet.ping.ServerListPing;
import net.hypejet.jet.protocol.packet.server.status.ServerListResponseStatusPacket;
import net.hypejet.jet.protocol.packet.server.status.ServerPingResponseStatusPacket;
import net.hypejet.jet.server.test.network.protocol.packet.server.ServerPacketTestUtil;
import net.kyori.adventure.text.Component;
import net.kyori.adventure.text.format.NamedTextColor;
import net.kyori.adventure.text.format.TextDecoration;
import org.junit.jupiter.api.Test;

import java.util.List;
import java.util.UUID;

/**
 * Represents a test of reading and writing of {@linkplain net.hypejet.jet.protocol.packet.server.ServerPacket server
 * packets}.
 *
 * @since 1.0
 * @author Codestech
 * @see net.hypejet.jet.protocol.packet.server.ServerPacket
 */
public final class ServerStatusPacketsTest {

    private static final String TEST_FAVICON = "";

    @Test
    public void testServerListResponse() {
        ServerPacketTestUtil.testPacket(ServerListResponseStatusPacket.class, new ServerListResponseStatusPacket(
                new ServerListPing(
                        new ServerListPing.Version("1.21", 766),
                        new ServerListPing.Players(20, 3, List.of(
                                new ServerListPing.PingPlayer("DummyPlayer", UUID.randomUUID()),
                                new ServerListPing.PingPlayer("AnotherPlayer", UUID.randomUUID())
                        )),
                        Component.text()
                                .append(Component.text("Hi!", NamedTextColor.RED))
                                .appendNewline()
                                .append(Component.text("Hello world!", NamedTextColor.GREEN, TextDecoration.BOLD))
                                .build(),
                        new ServerListPing.Favicon(TEST_FAVICON),
                        true, true, null
                )
        ));
    }

    @Test
    public void testPingResponse() {
        ServerPacketTestUtil.testPacket(ServerPingResponseStatusPacket.class,
                new ServerPingResponseStatusPacket(325254733));
    }
}